mkTriples :: [a] -> [(a,a,a)]
mkTriples []            = []
mkTriples (x:[])        = error "mkTriples: length not divisible by 3"
mkTriples (x1:x2:[])    = error "mkTriples: length not divisible by 3"
mkTriples (x1:x2:x3:xs) = (x1,x2,x3):mkTriples xs

extendLeft :: (Double, Double, Double) -> (Double, Double, Double)
extendLeft (x,y,z) = (if x < 0 then x - 0.5 else x, y, z)

flattenTriples :: [(a,a,a)] -> [a]
flattenTriples []              = []
flattenTriples ((x1,x2,x3):xs) = x1:x2:x3:flattenTriples xs

trans = flattenTriples . map extendLeft . mkTriples $ coords